submit_errors_list.value:
if($data.formOutput && $data.formOutput._submitError && $data.formOutput._submitError.explanations){
	const liElements = $data.formOutput._submitError.explanations
		.filter(cause => cause !== null)
		.map(cause => "<li>" + cause + "</li>")
		.join('');
	if(liElements){
		return "<ul>" + liElements + "</ul>";
	}
}

formOutput.value:
if( $data.order && $data.order_employee ){
	var output = {
		'orderInput': $data.order,
		'orderIsValid': $data.formInput.orderIsValid
	};
	output.orderInput.employee = $data.order_employee;
	return output;
}
